  "\u0406 \u0434\u0435\u043D\u044C \u0456\u0434\u0435, \u0456 \u043D\u0456\u0447 \u0456\u0434\u0435"
  "\u0422\u0435\u0447\u0435 \u0432\u043E\u0434\u0430 \u0437-\u043F\u0456\u0434 \u044F\u0432\u043E\u0440\u0430\u2026"
