  \u0422\u0438\u0442\u0430\u0440\u0456\u0432\u043D\u0430
  "\u0425\u043E\u0447\u0430 \u043B\u0435\u0436\u0430\u0447\u043E\u0433\u043E \u0439 \u043D\u0435 \u0431'\u044E\u0442\u044C"
  "\u0406 \u0442\u0443\u0442, \u0456 \u0432\u0441\u044E\u0434\u0438 \u2014 \u0441\u043A\u0440\u0456\u0437\u044C \u043F\u043E\u0433\u0430\u043D\u043E"
  "\u041E, \u043B\u044E\u0434\u0438! \u043B\u044E\u0434\u0438 \u043D\u0435\u0431\u043E\u0440\u0430\u043A\u0438!"
